mapper.select:
	 	SELECT *
	 		FROM (
	 				SELECT sub.*, rownum AS rnum
	 				FROM( 
	 					SELECT * FROM replys
	 					WHERE comment_no = #{superNo}
	 				) sub 
	 			)
 		
 			WHERE rnum BETWEEN #{to} AND #{from}

mapper.update:
UPDATE replys SET
reply_content = #{replyContent}
WHERE reply_no = #{replyNo}
