mapper.select:
SELECT COUNT(*)
FROM lease_transfer
WHERE accept_user_id = #{acceptUserId}
AND TO_DATE(TO_CHAR(SYSDATE, 'YYYY-MM-DD')) < to_date;

SELECT *
FROM (
		SELECT sub.*, rownum AS lease_transfer_row_num
		FROM( SELECT *						 
				FROM lease_transfer
				WHERE accept_user_id = #{userId}
				AND TO_DATE(TO_CHAR(SYSDATE, 'YYYY-MM-DD')) < to_date;
			) sub
		)

	WHERE lease_transfer_row_num BETWEEN #{from} AND #{to}

SELECT *
FROM lease_transfer
WHERE lease_transfer_no = #{leaseTransferNo} AND accept_user_id = #{acceptUserId}

mapper.delete:
DELETE FROM lease_transfer
WHERE lease_transfer_no = #{LeaseTransferNo}
AND	permission_date IS NULL

mapper.update:
UPDATE lease_transfer SET
	permission_date = SYSDATE
WHERE lease_transfer_no = #{leaseTransferNo}
